+    output.startList();
+    output.writeBytes(nodeKey);
+    output.writeBytes(node.getValue().orElse(Bytes.EMPTY));
+    output.endList();
+
+    try {
+      accountFileWriter.writeBytes(output.encoded().toArrayUnsafe());
+    } catch (final IOException ioe) {
