+        new StoredMerklePatriciaTrie<>(
+            worldStateStorage::getAccountStateTrieNode,
+            account.getStorageRoot(),
+            Function.identity(),
+            Function.identity());
+    storageTrie.visitLeafs(
+        (storageKey, storageValue) ->
