               if ((supportsSegmentation = localKeyStorageType.equals(serverKeyStorageType))) {
                  dataFormatBuilder.keyType(getMediaType(localKeyStorageType, marshaller));
                  dataFormatBuilder.keyMarshaller(getMarshaller(localKeyStorageType, marshaller));
               } else if (configuration.segmented()) {
                  throw log.segmentationRequiresEqualMediaTypes(localKeyStorageType, serverKeyStorageType);
               }
               // We store MarshalledEntry instances for the values when raw values is not set, instead of the stored value
               if (!configuration.rawValues()) {
                  localValueStorageType = MediaType.APPLICATION_OBJECT;
               }
               if (localValueStorageType.equals(serverValueStorageType)) {
                  dataFormatBuilder.valueType(getMediaType(localKeyStorageType, marshaller));
                  dataFormatBuilder.valueMarshaller(getMarshaller(localKeyStorageType, marshaller));
               }
...
   private MediaType getMediaType(MediaType local, Marshaller marshaller) {
      return local.isBinary() ? local : marshaller.mediaType();
   }

   private Marshaller getMarshaller(MediaType local, Marshaller marshaller) {
      return local.isBinary() ? IdentityMarshaller.INSTANCE : marshaller;
   }
Or we could just have the ternary operator inline.

