 package org.optaplanner.persistence.jsonb.api.score;

-import javax.json.bind.serializer.JsonbDeserializer;
+import javax.json.bind.adapter.JsonbAdapter;

 import org.optaplanner.core.api.score.Score;

 /**
- * JSONB binding support for a {@link Score} type.
+ * JSON-B binding support for a {@link Score} type.
  * <p>
- * For example: use {@code @JsonbTypeSerializer(HardSoftScoreJsonbSerializer.class) @JsonbTypeDeserializer(HardSoftScoreJsonbDeserializer.class)}
- * on a {@code HardSoftScore score} field and it will marshalled to JSON as {@code "score":"-999hard/-999soft"}.
+ * For example: use {@code @JsonbTypeAdapter(HardSoftScoreJsonbAdapter.class)}
+ * on a {@code HardSoftScore score} field and it will serialized to JSON as {@code "score":"-999hard/-999soft"}.
  * @see Score
  * @param <Score_> the actual score type
  */
-public abstract class AbstractScoreJsonbDeserializer<Score_ extends Score<Score_>>
-        implements JsonbDeserializer<Score_> {
+public abstract class AbstractScoreJsonbAdapter<Score_ extends Score<Score_>> implements JsonbAdapter<Score_, String> {

+    @Override
+    public String adaptToJson(Score_ score) {
+        return score.toString();
+    }
 }

