+        else
+        {
+            // Close colony
+            int closeClonyID = findNextNearbyColony(world, pos, MineColonies.getConfig().getCommon().minTownHallPadding.get());
+
+            if (closeClonyID != 0)
+            {
+                findPaneOfTypeByID(TEXT_NEARBY, Text.class).setTextContent(LanguageHandler.format("com.minecolonies.coremod.gui.colony.near", closeClonyID));
+            }
+            else
+            {
