+    public static ColdStorageContentStatus checkAvailabilityOfColdStorageContent(CoreSession session) {
+        log.debug("Start checking the available cold storage content which are being retrieved for repository: {}",
+                session::getRepositoryName);
+
+        // as the volume of result will be small, we don't use BAF
+        DocumentModelList documents = session.query(GET_DOCUMENTS_TO_CHECK_QUERY);
+
+        // for every available content we will fire an event
+        int available = 0;
+        EventService eventService = Framework.getService(EventService.class);
+        for (DocumentModel doc : documents) {
+            if (isColdStorageContentAvailable(doc)) {
+                available++;
+                DocumentEventContext ctx = new DocumentEventContext(session, session.getPrincipal(), doc);
+                Event event = ctx.newEvent(COLD_STORAGE_CONTENT_AVAILABLE_EVENT_NAME);
+                eventService.fireEvent(event);
+            }
+        }
+
+        int total = documents.size();
+        log.debug("End checking the available cold storage content for repository: {}, found: {}/{}",
+                session.getRepositoryName(), available, total);
+
+        return new ColdStorageContentStatus(session.getRepositoryName(), total, available);
