+        return getOperationResponse(operationId, input, params, null, null, valueType);
+    }
+
+    protected boolean getOperationBoolean(String operationId, Map<String, Object> params) {
+        JsonNode node = getOperationResponse(operationId, null, params, null, null, JsonNode.class);
+        assertEquals("boolean", node.get("entity-type").asText());
+        return node.get("value").asBoolean();
+    }
+
+    protected String getOperationResponse(String operationId, Object input, Map<String, Object> params,
+            Map<String, Object> contextParams, Map<String, String> headers) {
+        Function<InputStream, String> bodyReader = stream -> {
+            try {
+                return IOUtils.toString(stream, UTF_8);
+            } catch (IOException e) {
+                throw new NuxeoException(e);
+            }
+        };
+        return getOperationResponse(operationId, input, params, contextParams, headers, bodyReader);
+    }
+
+    protected <T> T getOperationResponse(String operationId, Object input, Map<String, Object> params,
+            Map<String, Object> contextParams, Map<String, String> headers, Class<T> valueType) {
+        Function<InputStream, T> bodyReader = stream -> {
+            try {
+                return mapper.readValue(stream, valueType);
+            } catch (IOException e) {
+                throw new NuxeoException(e);
+            }
+        };
+        return getOperationResponse(operationId, input, params, contextParams, headers, bodyReader);
+    }
