+                InputStream stream = response.getEntityInputStream()) {
+            int status = response.getStatus();
+            if (status == SC_NO_CONTENT) {
+                return null;
+            } else {
+                assertEquals(SC_OK, status);
+                return bodyReader.apply(stream);
+            }
+        } catch (IOException e) {
+            throw new NuxeoException(e);
+        }
+    }
