+        String acceptedType = MediaType.APPLICATION_JSON + ", */*";
+        String data = getAutomationBody(input, params, contextParams);
+        try (CloseableClientResponse response = getResponse(RequestType.POST, operationId, data, null, null, schemas,
+                headers, acceptedType); //
+                InputStream stream = response.getEntityInputStream()) {
+            assertEquals(expectedStatus, response.getStatus());
+            JsonNode node = mapper.readTree(stream);
+            if (node == null) {
+                return null;
+            }
+            JsonNode messageNode = node.get("message");
+            return messageNode == null ? null : messageNode.asText();
+        } catch (IOException e) {
+            throw new NuxeoException(e);
+        }
+    }
+
+    protected String getAutomationBody(Object input, Map<String, Object> params, Map<String, Object> contextParams) {
+        Map<String, Object> map = new HashMap<>();
+        if (input instanceof String) {
+            map.put("input", input);
+        }
+        if (params == null) {
+            params = Collections.emptyMap();
+        }
+        map.put("params", params);
+        if (contextParams == null) {
+            contextParams = Collections.emptyMap();
+        }
+        map.put("context", contextParams);

