+                        break;
+                    case DOWTOD:
+                        break;
+                }
+          
+                if (state != null) {
+                    logger.debug("Received update for channel '{}': {}", channelId, state.toFullString());
+                    updateState(channelId, state);
+                }
+            }
+        }
+    }
