+        if (!explicitDisconnect && config.connectionRetryInterval > 0) {
+            scheduler.schedule(() -> {
+                if (connector != null && !connecting && !connector.isConnected()) {
+                    connect();
+                }
+            }, config.connectionRetryInterval, TimeUnit.SECONDS);
+        }    
+    }
