+                    startAutomaticRefresh();
+                    return;
+                }
+            }
+            logger.debug("GREE unit is not responding");
+        } catch (GreeException e) {
+            logger.debug("Initialization failed: {}", messages.get("thinginit.exception", e.toString()));
+        } catch (IOException | RuntimeException e) {
+            logger.debug("Exception on inituialization", e);
+        }
+
+        updateStatus(ThingStatus.OFFLINE);
+    }
+
+    @Override
+    public void handleCommand(ChannelUID channelUID, Command command) {
+        if (command instanceof RefreshType) {
+            // The thing is updated by the scheduled automatic refresh so do nothing here.
+        } else {
+            if (!clientSocket.isPresent()) {
+                logger.debug("Thing not properly initialized, abort command");
+                return;
+            }
+
+            DatagramSocket socket = clientSocket.get();
+            logger.debug("Issue command {} to channe {}", command, channelUID.getIdWithoutGroup());
+            try {
+                switch (channelUID.getIdWithoutGroup()) {
+                    case MODE_CHANNEL:
+                        handleModeCommand(command, socket);
+                        break;
+                    case POWER_CHANNEL:
+                        device.setDevicePower(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+                        break;
+                    case TURBO_CHANNEL:
+                        device.setDeviceTurbo(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+                        break;
+                    case LIGHT_CHANNEL:
+                        device.setDeviceLight(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+                        break;
+                    case TEMP_CHANNEL:
+                        device.setDeviceTempSet(socket, ((DecimalType) command).intValue());
+                        break;
+                    case SWINGV_CHANNEL:
+                        device.setDeviceSwingVertical(socket, ((DecimalType) command).intValue());
+                        break;
+                    case WINDSPEED_CHANNEL:
+                        device.setDeviceWindspeed(socket, ((DecimalType) command).intValue());
+                        break;
+                    case AIR_CHANNEL:
+                        device.setDeviceAir(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+                        break;
+                    case DRY_CHANNEL:
+                        device.setDeviceDry(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+                        break;
+                    case HEALTH_CHANNEL:
+                        device.setDeviceHealth(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+                        break;
+                    case PWRSAV_CHANNEL:
+                        device.setDevicePwrSaving(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+                        break;
+                }
+            } catch (GreeException e) {
+                logger.debug("Unable to execute command {} for channel {}: {}", command, channelUID.getId(),
+                        e.toString());
+            } catch (RuntimeException e) {
+                logger.debug("Unable to execute command {} for channel {}", command, channelUID.getId(), e);
+            }
+        }
+    }
+
+    private void handleModeCommand(Command command, DatagramSocket socket) throws GreeException {
+        int mode = -1;
+        String modeStr = "";
+        boolean isNumber = StringUtils.isNumeric(command.toString());
+        if (command instanceof DecimalType) {
+            // backward compatibility when channel was Number
+            mode = ((DecimalType) command).intValue();
+        } else if (command instanceof OnOffType) {
+            // Switch
+            logger.debug("Send Power-{}", command);
+            device.setDevicePower(socket, (OnOffType) command == OnOffType.ON ? 1 : 0);
+        } else /* String */ {
+            modeStr = command.toString().toLowerCase();
+            switch (modeStr) {
+                case MODE_AUTO:
+                    mode = GREE_MODE_AUTO;
+                    break;
+                case MODE_COOL:
+                    mode = GREE_MODE_COOL;
+                    break;
+                case MODE_HEAT:
+                    mode = GREE_MODE_HEAT;
+                    break;
+                case MODE_DRY:
+                    mode = GREE_MODE_DRY;
+                    break;
+                case MODE_FAN:
+                case MODE_FAN2:
+                    mode = GREE_MODE_FAN;
+                    break;
+                case MODE_ECO:
+                    // power saving will be set after the uinit was turned on
+                    mode = GREE_MODE_COOL;
+                    break;
+                case MODE_ON:
+                case MODE_OFF:
+                    device.setDevicePower(socket, modeStr.equals(MODE_ON) ? 1 : 0);
+                    break;
+                default:
+                    if (isNumber) {
+                        // Support selecting the mode by number, maybe specific models support
+                        // additional modes
+                        mode = Integer.parseInt(modeStr);
+                    } else {
+                        logger.debug("Invalid mode requested: {}", command);
+                    }
+                    break;
+            }
+            logger.debug("Mode {} mapped to {}", modeStr, mode);
+        }
+
+        if (mode != -1) {
+            // Turn on the unit if currently off
+            if (!isNumber && (device.getIntStatusVal("Pow") == 0)) {
+                logger.debug("Send Auto-ON for mode {}", mode);
+                device.setDevicePower(socket, 1);
+            }
+
+            // Select mode
+            logger.debug("Select mode {}", mode);
+            device.SetDeviceMode(socket, mode);
+
+            // Check for secondary action
+            switch (modeStr) {
+                case MODE_ECO:
+                    // Turn on power saving for eco mode
+                    logger.debug("Turn on Power-Saving");
+                    device.setDevicePwrSaving(socket, 1);
+                    break;
+                case MODE_TURBO:
+                    device.setDeviceTurbo(socket, 1);
+                    break;
+            }
+        }
+    }
+
+    private boolean isMinimumRefreshTimeExceeded() {
+        long currentTime = System.currentTimeMillis();
+        long timeSinceLastRefresh = currentTime - lastRefreshTime;
+        if (timeSinceLastRefresh < MINIMUM_REFRESH_TIME_MS) {
+            return false;
+        }
+        lastRefreshTime = currentTime;
+        return true;
+    }
+
+    private void startAutomaticRefresh() {
+
+        Runnable refresher = new Runnable() {
+            @Override
+            public void run() {
+
+                try {
+                    logger.debug("Executing automatic update of values");
+                    // safeguard for multiple REFRESH commands
+                    if (isMinimumRefreshTimeExceeded()) {
+                        logger.debug("Fetching status values from device.");
+                        // Get the current status from the Airconditioner
+                        device.getDeviceStatus(clientSocket.get());
+                    } else {
+                        logger.trace(
+                                "Skipped fetching status values from device because minimum refresh time not reached");
+                    }
+
+                    // Update All Channels
+                    List<Channel> channels = getThing().getChannels();
+                    for (Channel channel : channels) {
+                        publishChannel(channel.getUID());
+                    }
+                } catch (GreeException e) {
+                    if (!e.isTimeout()) {
+                        logger.debug("Unable to perform auto-update: {}", e.toString());
+                    }
+                } catch (RuntimeException e) {
+                    logger.debug("Unable to perform auto-update", e);
+                }
+            }
+        };
+
+        refreshTask = scheduler.scheduleWithFixedDelay(refresher, 0, config.refresh, TimeUnit.SECONDS);
+        logger.debug("Automatic refresh started ({} second interval)", config.refresh);
+    }
+
+    private void publishChannel(ChannelUID channelUID) throws GreeException {
+        try {
+            String channelID = channelUID.getId();
+            Optional<State> state = Optional.empty();
+            switch (channelUID.getIdWithoutGroup()) {
+                case POWER_CHANNEL:
+                    state = updateOnOff("Pow");
+                    break;
+                case MODE_CHANNEL:
+                    state = updateMode();
+                    break;
+                case TURBO_CHANNEL:
+                    state = updateOnOff("Tur");
+                    break;
+                case LIGHT_CHANNEL:
+                    state = updateOnOff("Lig");
+                    break;
+                case TEMP_CHANNEL:
+                    state = updateTemp("SetTem");
