+        HashMap<String, HashMap<String, Integer>> tempRanges = new HashMap<>();
+        HashMap<String, Integer> hmf = new HashMap<>();
+        HashMap<String, Integer> hmc = new HashMap<>();
+
+        hmf.put("min", 61); // F
+        hmf.put("max", 86);
+        tempRanges.put("F", hmf);
+
+        hmc.put("min", 16); // C
+        hmc.put("max", 30);
+        tempRanges.put("C", hmc);
+
+        return tempRanges;
+    }
