+                                                    // string for hashmap
+        HashMap<String, Integer> nullCorFLUT = new HashMap<>(); // simple look up table for logic
+        nullCorFLUT.put("C", 0);
+        nullCorFLUT.put("F", 1);
+        nullCorFLUT.put("INVALID", 0);
