+                    case AIR_CHANNEL:
+                        device.setDeviceAir(socket, getOnOff(command));
+                        break;
+                    case DRY_CHANNEL:
+                        device.setDeviceDry(socket, getOnOff(command));
+                        break;
+                    case HEALTH_CHANNEL:
