+                logger.warn("{}: Invalid command value {} for channel {}", thingId, command, channelUID.getId());
+            } catch (RuntimeException e) {
+                logger.warn("{}: Unable to execute command {} for channel {}", thingId, command, channelId, e);
+            }
+        }
+    }
+
+    private void handleModeCommand(DatagramSocket socket, Command command) throws GreeException {
+        int mode = -1;
+        String modeStr = "";
+        boolean isNumber = false;
+        if (command instanceof DecimalType) {
+            // backward compatibility when channel was Number
+            mode = ((DecimalType) command).intValue();
+        } else if (command instanceof OnOffType) {
+            // Switch
+            logger.debug("{}: Send Power-{}", thingId, command);
+            device.setDevicePower(socket, getOnOff(command));
+        } else /* String */ {
+            modeStr = command.toString().toLowerCase();
+            switch (modeStr) {
+                case MODE_AUTO:
+                    mode = GREE_MODE_AUTO;
+                    break;
+                case MODE_COOL:
+                    mode = GREE_MODE_COOL;
+                    break;
+                case MODE_HEAT:
+                    mode = GREE_MODE_HEAT;
+                    break;
+                case MODE_DRY:
+                    mode = GREE_MODE_DRY;
+                    break;
+                case MODE_FAN:
+                case MODE_FAN2:
+                    mode = GREE_MODE_FAN;
+                    break;
+                case MODE_ECO:
+                    // power saving will be set after the uinit was turned on
+                    mode = GREE_MODE_COOL;
+                    break;
+                case MODE_ON:
+                case MODE_OFF:
+                    logger.debug("{}: Turn unit {}", thingId, modeStr);
+                    device.setDevicePower(socket, modeStr.equals(MODE_ON) ? 1 : 0);
