+            resp.decryptedPack = GreeCryptoUtil.decryptPack(getKey(), resp.pack);
+            logger.debug("Response from device: {}", resp.decryptedPack);
+            resp.packJson = gson.fromJson(resp.decryptedPack, GreeStatusResponsePackDTO.class);
