+            final String content = contentResponse.getContentAsString();
+            logger.trace("Pushover response: status = {}, content = '{}'", httpStatus, content);
+            switch (httpStatus) {
+                case HttpStatus.OK_200:
+                    return content;
+                case HttpStatus.BAD_REQUEST_400:
