revised_code.chunk:
diff --git a/operator/src/main/java/oracle/kubernetes/operator/helpers/KubernetesUtils.java b/operator/src/main/java/oracle/kubernetes/operator/helpers/KubernetesUtils.java
index 4b25f24b74..8fefc92230 100644
--- a/operator/src/main/java/oracle/kubernetes/operator/helpers/KubernetesUtils.java
+++ b/operator/src/main/java/oracle/kubernetes/operator/helpers/KubernetesUtils.java

@@ -75,11 +75,16 @@ public class KubernetesUtils {
         String basePath,
         Map<String, String> current,
         Map<String, String> required) {
+
     for (String name : required.keySet()) {
+      // We must encode each '/' and '~' in a JSON patch token using '~1' and '~0', otherwise
+      // the JSON patch will incorrectly treat '/' and '~' as special delimiters. (RFC 6901).
+      // The resulting patched JSON will have '/' and '~' within the token (not ~0 or ~1).
+      String encodedPath = basePath + name.replace("~","~0").replace("/","~1");
       if (!current.containsKey(name)) {
-        patchBuilder.add(basePath + name.replace("/", "~1"), required.get(name));
+        patchBuilder.add(encodedPath, required.get(name));
       } else {
-        patchBuilder.replace(basePath + name.replace("/", "~1"), required.get(name));
+        patchBuilder.replace(encodedPath, required.get(name));
       }
     }
   }
