+package tc.oc.pgm.stats;
+
+import static tc.oc.pgm.stats.StatsMatchModule.numberComponent;
+
+import java.util.ArrayList;
+import java.util.List;
+import net.kyori.text.Component;
+import net.kyori.text.TranslatableComponent;
+import net.kyori.text.format.TextColor;
+import net.md_5.bungee.api.ChatColor;
+import org.bukkit.Material;
+import org.bukkit.entity.Player;
+import org.bukkit.event.inventory.ClickType;
+import org.bukkit.inventory.ItemFlag;
+import org.bukkit.inventory.ItemStack;
+import org.bukkit.inventory.meta.SkullMeta;
+import tc.oc.pgm.api.player.MatchPlayer;
+import tc.oc.pgm.menu.InventoryMenu;
+import tc.oc.pgm.menu.InventoryMenuItem;
+import tc.oc.pgm.util.text.TextTranslations;
+
+public class PlayerStatsInventoryMenuItem implements InventoryMenuItem {
+
+  private final MatchPlayer player;
+  private final TextColor RESET = TextColor.WHITE;
+
+  PlayerStatsInventoryMenuItem(MatchPlayer player) {
+    this.player = player;
+  }
+
+  @Override
+  public Component getName() {
+    return player.getName();
+  }
+
+  @Override
+  public ChatColor getColor() {
+    return ChatColor.GOLD;
+  }
+
+  @Override
+  public List<String> getLore(MatchPlayer player) {
+    List<String> lore = new ArrayList<>();
+    StatsMatchModule smm = player.getMatch().needModule(StatsMatchModule.class);
+    StatsMatchModule.PlayerStats stats = smm.getPlayerStat(player.getId());
+
+    Component killLore =
+        TranslatableComponent.of(
+            "match.stats.kills.concise", RESET, numberComponent(stats.kills, TextColor.GREEN));
+    Component deathLore =
+        TranslatableComponent.of(
+            "match.stats.deaths.concise", RESET, numberComponent(stats.deaths, TextColor.RED));
+    Component kdLore =
+        TranslatableComponent.of(
+            "match.stats.kd.concise", RESET, numberComponent(stats.getKD(), TextColor.GREEN));
+    Component killstreakLore =
+        TranslatableComponent.of(
+            "match.stats.killstreak.concise",
+            RESET,
+            numberComponent(stats.killstreakMax, TextColor.GREEN));
+    Component damageLore =
+        TranslatableComponent.of(
+            "match.stats.damage.concise",
+            RESET,
+            numberComponent(stats.getDamageDone(), TextColor.GREEN),
+            numberComponent(stats.getBowDamage(), TextColor.YELLOW),
+            numberComponent(stats.getDamageTaken(), TextColor.RED));
+    Component bowLore =
+        TranslatableComponent.of(
+            "match.stats.bow.concise",
+            RESET,
+            numberComponent(stats.shotsTaken, TextColor.YELLOW),
+            numberComponent(stats.getArrowAccuracy(), TextColor.YELLOW));
+
+    Player bukkit = player.getBukkit();
+
+    lore.add(TextTranslations.translateLegacy(killLore, bukkit));
+    lore.add(TextTranslations.translateLegacy(deathLore, bukkit));
+    lore.add(TextTranslations.translateLegacy(kdLore, bukkit));
+    lore.add(TextTranslations.translateLegacy(killstreakLore, bukkit));
+    lore.add(TextTranslations.translateLegacy(damageLore, bukkit));
+    lore.add(TextTranslations.translateLegacy(bowLore, bukkit));
+
+    optionalStat(lore, stats.flagsCaptured, "match.stats.flagsCaptured.concise", bukkit);
+    optionalStat(lore, stats.destroyablePiecesBroken, "match.stats.broken.concise", bukkit);
+    optionalStat(lore, stats.getLongestFlagHold(), "match.stats.flaghold.concise", bukkit);
+
+    return lore;
+  }
+
+  private void optionalStat(List<String> lore, int stat, String key, Player bukkit) {
+    if (stat > 0) {
+      lore.add(null);
+      Component loreComponent =
+          TranslatableComponent.of(key, RESET, numberComponent(stat, TextColor.AQUA));
+      lore.add(TextTranslations.translateLegacy(loreComponent, bukkit));
+    }
+  }
+
+  private void optionalStat(List<String> lore, String stat, String key, Player bukkit) {
+    if (Double.parseDouble(stat) > 0) {
+      lore.add(null);
+      Component loreComponent =
+          TranslatableComponent.of(key, RESET, numberComponent(stat, TextColor.AQUA));
+      lore.add(TextTranslations.translateLegacy(loreComponent, bukkit));
+    }
+  }

