+    }
+
+    private void setLongestFlagHold(Duration time) {
+      if (longestFlagHold == null || (time.toNanos() - longestFlagHold.toNanos()) > 0)
+        longestFlagHold = time;
+    }
+
+    private void setLongestBowKill(double distance) {
+      if (distance > longestBowKill) {
+        longestBowKill = (int) Math.ceil(distance);
+      }
+    }
+
+    private void onBowShoot() {
+      shotsTaken++;
+    }
+
+    /** A common format used by all stats with decimals */
