+        Player player = e.getPlayer();
+        
+        if (!player.isGliding()) return;
+        if (player.isOnGround()) return;
+        
+        ItemStack chestplate = player.getInventory().getChestplate();
+        if (chestplate.getType() != Material.ELYTRA) return;

