+    } else {
+      if (storedCombatMoveMap == null) {
+        combatMoveAi.doCombatMove(moveDel);
+      } else {
+        combatMoveAi.doMove(storedCombatMoveMap, moveDel, data, player);
+        storedCombatMoveMap = null;
+      }
+    }
