+            List<Move> solution = solutions.get(solutionKey);
+            if (solution == null) {
+                solution = findRoomFor(node, host, hosts, movesConsidered, movesMade);
+                solutions.put(solutionKey, solution);
+            }
+            return solution;
+        }
