+
+/**
+ * Implementation for {@link ServiceURLBuilder}.
+ * Builder for {@link ServiceURL} instances.
+ */
+public class DefaultServiceURLBuilder implements ServiceURLBuilder {
+
+    private Boolean addProxyContext;
+    private Boolean addWebContext;
+    private String urlContext;
+    private Boolean appendTenantAsQueryParam;
+    private Boolean appendTenantAsPathParam;
+    private Map<String, Object> parameters;
+    private Map<String, Object> fragments;
+
+    @Override
+    public ServiceURLBuilder addContext(String context) {
+
+        this.urlContext = context;
+        return this;
+    }
+
+    @Override
+    public final ServiceURLBuilder addProxyContext() {
+
+        this.addProxyContext = true;
+        return this;
+    }
+
+    @Override
+    public final ServiceURLBuilder addWebContext() {
+
+        this.addWebContext = true;
+        return this;
+    }
+
+    @Override
+    public ServiceURLBuilder appendTenantAsQueryParam() {
+
+        this.appendTenantAsQueryParam = true;
+        return this;
+    }
+
+    @Override
+    public ServiceURLBuilder appendTenantAsPathParam() {
+
+        this.appendTenantAsPathParam = true;
+        return this;
+    }
+
+    @Override
+    public ServiceURLBuilder addParameter(String key, Object value) {
+
+        return null;
+    }
+
+    @Override
+    public ServiceURL build() throws URLResolverException {
+
+        String protocol = fetchProtocol();
+        String hostName = fetchHostName();
+        Integer port = fetchPort();
+        String proxyContext = null;
+        if (addProxyContext) {
+            proxyContext = fetchProxyContextPath();
+        }
+        String webContext = null;
+        if (addWebContext) {
+            webContext = fetchWebContextRoot();
+        }
+
+        String urlContext = this.urlContext;
+
+        String tenantDomain = IdentityTenantUtil.getTenantDomainFromContext();
+        if (StringUtils.isBlank(tenantDomain)) {
+            tenantDomain = PrivilegedCarbonContext.getThreadLocalCarbonContext().getTenantDomain();
+        }
+
+        String resolvedUrlContext = null;
+        StringBuilder resolvedUrlStringBuilder = new StringBuilder();
+        if (isTenantQualifiedUrlsEnabled()) {
+            if (StringUtils.isNotBlank(tenantDomain)) {
+                resolvedUrlStringBuilder.append("/t/").append(tenantDomain);
+            }
+            resolvedUrlContext = resolveUrlFromStandardContext(urlContext);
+        }
+
+        if (!isTenantQualifiedUrlsEnabled() && appendTenantAsPathParam) {
+            if (StringUtils.isNotBlank(tenantDomain) &&
+                    !MultitenantConstants.SUPER_TENANT_DOMAIN_NAME.equalsIgnoreCase(tenantDomain)) {
+                resolvedUrlStringBuilder.append("/t/").append(tenantDomain);
+            }
+        }
+
+        if (StringUtils.isBlank(resolvedUrlContext)) {
+            resolvedUrlContext = resolveUrlFromServerConfig(urlContext);
+        }
+
+        if (StringUtils.isNotBlank(resolvedUrlContext)) {
+            resolvedUrlStringBuilder.append(resolvedUrlContext);
+        }
+
+        if (!isTenantQualifiedUrlsEnabled() && appendTenantAsQueryParam) {
+            if (StringUtils.isNotBlank(tenantDomain) &&
+                    !MultitenantConstants.SUPER_TENANT_DOMAIN_NAME.equalsIgnoreCase(tenantDomain)) {
+                resolvedUrlStringBuilder.append("?").append(MultitenantConstants.TENANT_DOMAIN).append("=")
+                        .append(tenantDomain);
+            }
+        }
+
+        Map<String, Object> parameters = this.parameters;
+        Map<String, Object> fragments = this.fragments;
+
+        return new ServiceURL(protocol, hostName, port, proxyContext, webContext, resolvedUrlStringBuilder.toString(),
+                parameters, fragments);
+
+    }
+
+    private String fetchProtocol() {
+
+        return CarbonUtils.getManagementTransport();
+    }
+
+    private String fetchHostName() throws URLResolverException {
+
+        String hostName = ServerConfiguration.getInstance().getFirstProperty(IdentityCoreConstants.HOST_NAME);
+        try {
+            if (StringUtils.isBlank(hostName)) {
+                hostName = NetworkUtils.getLocalHostname();
+            }
+        } catch (SocketException e) {
+            throw new URLResolverException("Error while trying to resolve the hostname from the system.", e);
+        }
+        return hostName;
+    }
+
+    private Integer fetchPort() {
+
+        String mgtTransport = CarbonUtils.getManagementTransport();
+        AxisConfiguration axisConfiguration = IdentityCoreServiceComponent.getConfigurationContextService().
+                getServerConfigContext().getAxisConfiguration();
+        int port = CarbonUtils.getTransportProxyPort(axisConfiguration, mgtTransport);
+        if (port <= 0) {
+            port = CarbonUtils.getTransportPort(axisConfiguration, mgtTransport);
+        }
+        return port;
+    }
+
+    private String fetchProxyContextPath() {
+
+        return ServerConfiguration.getInstance().getFirstProperty(IdentityCoreConstants.PROXY_CONTEXT_PATH);
+    }
+
+    private String fetchWebContextRoot() {
+
+        return ServerConfiguration.getInstance().getFirstProperty(IdentityCoreConstants.WEB_CONTEXT_ROOT);
+    }
+
+    private String resolveUrlFromStandardContext(String urlContext) {
+        /**
+         * TODO
+         * resolve URL context from the standard context.
+         *
+         */
+        return urlContext;
+    }

