+            if (contextPath.trim().charAt(0) != '/') {
+                serverUrl.append("/").append(contextPath.trim());
+            } else {
+                serverUrl.append(contextPath.trim());
+            }
+        }
+    }
+
+    private void appendParamsToUri(StringBuilder serverUrl, Map<String, String> parameters) {
+
+        if (MapUtils.isNotEmpty(parameters)) {
+            for (Map.Entry<String, String> entry : parameters.entrySet()) {
+                serverUrl.append("?").append(entry.getKey()).append("=").append(entry.getValue());
+            }
+        }
